export default function RootLayout({ children }) {
  const [submenu,setsubMenu] = useState(false);
  return (
    <html lang="en">
      <body className={inter.className}>
        <menuContext.Provider value={{submenu,setsubMenu}}>
          <Header/>
          {submenu?<Menu/>:''}
          {children}
        </menuContext.Provider>
      </body>
    </html>
  )
}
